import * as React from "react";
import { PropertyControls, ControlType } from "framer";



// Define type of property
interface Props {
    textL: string;
    textR: string;
    styleL: string;
    styleR: string;
}

export class Buttons extends React.Component<Props> {

    // Set default properties
    static defaultProps = {
    textL: "Left",
    textR: "Right",
    styleL:"outlinedGray",
    styleR:"normal",
    width: 375,
    height: 64,
    }

    // Items shown in property panel
    static propertyControls: PropertyControls = {
    textL: { type: ControlType.String, title: "Text L" },
    textR: { type: ControlType.String, title: "Text R" },

    styleL: {
        type: ControlType.Enum,
        options: ["normal","aciton","pressed","disabled","outlined","outlinedGray","red"],
        optionTitles: ["Normal","action","Pressed","Disabled","Outlined","Outlined-Gray","Normal-Red"],
        title: "Style L",
     },

     styleR: {
        type: ControlType.Enum,
        options: ["normal","action","pressed","disabled","outlined","outlinedGray","red"],
        optionTitles: ["Normal","action","Pressed","Disabled","Outlined","Outline-Gray","Normal-Red"],
        title: "Style R",
     },


    }

    render() {
    
    var styleL;
    styleL = styles[this.props.styleL];

    var styleR;    
    styleR = styles[this.props.styleR];

    return <div style={styles.wrapper}>
        <div style={styleL}>{this.props.textL}</div>
        <div style={styles.gap}></div>
        <div style={styleR}>{this.props.textR}</div>
        </div>;
    }
}


